import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { Emitters } from '../emitters/emitters';
import { CookieService } from 'ngx-cookie-service';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit {
  id:any|null;
  constructor(private http:HttpClient, private route: ActivatedRoute, private cookieService: CookieService){}
  productDetails:any;

  ngOnInit(): void {
    this.getProductDetails();
  }

  getProductDetails(): void {
    this.id = this.route.snapshot.paramMap.get('productid');
    this.http.get("https://localhost:44300/e-auction/api/v1/seller/show-bids?productId="+this.id)
        .subscribe(res=>{
            this.productDetails = res;
            console.log(res);
            Emitters.authEmitter.emit(true);
          }, 
          err=>{
            console.log(err);
            Emitters.authEmitter.emit(false);
            this.cookieService.delete('username');
            this.cookieService.delete('jwtToken');
          });
  }
}
<p>product-details</p>
<p>Product Id: {{id}}</p>
<table>
    <tr>
        <th>Product Name</th>
        <td> : </td>
        <td>{{productDetails.productName}}</td>
    </tr>
    <tr>
        <th>Short Description</th>
        <td> : </td>
        <td>{{productDetails.shortDescription}}</td>
    </tr>
    <tr>
        <th>Detailed Description</th>
        <td> : </td>
        <td>{{productDetails.detailedDescription}}</td>
    </tr>
    <tr>
        <th>Category</th>
        <td> : </td>
        <td>{{productDetails.category}}</td>
    </tr>
    <tr>
        <th>Starting Price</th>
        <td> : </td>
        <td>{{productDetails.startingPrice}}</td>
    </tr>
    <tr>
        <th>Bid EndDate</th>
        <td> : </td>
        <td>{{productDetails.bidEndDate}}</td>
    </tr>
</table>
