import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms'
import { HttpClient } from '@angular/common/http'
import { Router } from '@angular/router';
import { Emitters } from '../emitters/emitters';
import { CookieService } from 'ngx-cookie-service';

@Component({
  selector: 'app-make-bid',
  templateUrl: './make-bid.component.html',
  styleUrls: ['./make-bid.component.css']
})
export class MakeBidComponent {
  bidForm = new FormGroup({
    productName: new FormControl(''),
    shortDescription:new FormControl(''),
    detailedDescription:new FormControl(''),
    category:new FormControl(''),
    startingPrice:new FormControl(''),
    bidEndDate:new FormControl(''),
    sellerEmailId: new FormControl('')
  });
  user:string = `${this.cookieService.get('username')}`;

  constructor(
    private http:HttpClient,
    private router:Router,
    private cookieService: CookieService
  ){};

  onSubmit() {
    // TODO: Use EventEmitter with form value
    this.http.post("https://localhost:44300/e-auction/api/v1/seller/add-product", this.bidForm.value)
    .subscribe(res=>{
      Emitters.authEmitter.emit(true);
      this.router.navigate(['/']);
    },
    err=>{
      console.log(err);
      Emitters.authEmitter.emit(false);
      this.cookieService.delete('username');
      this.cookieService.delete('jwtToken');
    });
  }
}
